import AceEditorComponent from "./AceEditorComponent.jsx";
import brace from "brace";
import AceEditor from "react-ace";
import "brace/mode/java";
import "brace/theme/xcode";
//import reactFile from "./../../codeTypes/react.jsx"
import ResumeContainer from "./../../resume/ResumeContainer.js"
class AceEditorContainer extends Component {
	constructor(props) {
		super();
		this.state = {
            aceValue: ""
		};
	}

    returnModeByFileExtension = file => {return require(file)}
	onChange = newValue => {this.setState({ aceValue: newValue })};
	render() {
        let file = new FileReader();
        console.log( this.returnModeByFileExtension( ResumeContainer));
		const props = (
			<AceEditor
				mode={this.returnModeByFileExtension}
				theme="xcode"
                onChange={this.onChange}
                value={this.returnModeByFileExtension( ResumeContainer )}
                name="ace_text_editor"
                width={'100%'}
                focus={true}
				editorProps={{ $blockScrolling: true }}
            />
		)
		debugger
		return <AceEditorComponent {...props} />;
	}
}

export default AceEditorContainer;
