import React, { Component } from "react";
import AceEditorComponent from "./AceEditorComponent.jsx";
import brace from "brace";
import AceEditor from "react-ace";
import "brace/mode/java";
import "brace/theme/xcode";
// import "brace/ext/modelist";
// import "brace/ext/themelist";
import ResumeContainer from "./../../resume/ResumeContainer.js";
import * as ace from "ace-builds/src-noconflict/ext-modelist";

class AceEditorContainer extends Component {
	constructor(props) {
		super();
		this.state = {
			aceValue: ""
		};
	}

	onChange = newValue => {
		this.setState({ aceValue: newValue });
	};
	render() {
        //const modeList = brace.acequire("ace/ext/modelist");
        //const themeList = brace.acequire("brace/ext/themelist")
        //const mode = brace.getModeForPath(ResumeContainer).mode;

		//console.log(mode);
		const props = (
			<AceEditor
				mode={'java'}
				theme="xcode"
				onChange={this.onChange}
				value={this.state.aceValue}
				name="ace_text_editor"
				width={"100%"}
				focus={true}
				editorProps={{ $blockScrolling: true }}
			/>
		);

		return <AceEditorComponent {...props} />;
	}
}

export default AceEditorContainer;
