import React, { Component } from "react";
import AceEditorComponent from "./AceEditorComponent.jsx";
import GalleryContainer from "./../../codeTypes/react.jsx";
import brace from "brace";
import AceEditor from "react-ace";
// import "brace/mode/java";
import "brace/theme/monokai";
import "brace/ext/modelist";
import "brace/ext/themelist";
//  import "./doclist";

class AceEditorContainer extends Component {
	constructor(props) {
		super();
		this.state = {
			aceValue: ""
		};
	}

	onChange = newValue => {
		this.setState({ aceValue: newValue });
    };
    
	render() {
        const retrieveMyDocument = brace.acequire("ace/demo/kitchen-sink/doclist");
        const modeList = brace.acequire("ace/ext/modelist");
        const themeList = brace.acequire("ace/ext/themelist");
        const filePath = "./../../resume/ResumeContainer.js"
        //const documentation = new brace.createEditSession()
        //documentation.Document( {GalleryContainer});
        const mode = modeList.getModeForPath(filePath).name;
        const theme = themeList.themesByName[this.props.theme].name;
        //const setDocumentation = brace.getDocument();
        const editAceEditor = brace.edit(this.props.id);
        editAceEditor.getSession().setValue("hello");
        debugger
        //const loadFileIntoAceEditor = brace.createEditSession(code,mode);
		const props = (
			<AceEditor
				mode={mode}
                theme={theme}
				onChange={this.onChange}
                //value={editAceEditor}
				name={this.props.key}
				width={"100%"}
				focus={true}
				editorProps={{$blockScrolling: true}}
			/>
		);

		return <AceEditorComponent {...props} />;
	}
}

export default AceEditorContainer;
