import React, { Component } from "react";
import NavBarComponent from "./NavBarComponent.jsx";
import { Menu } from "semantic-ui-react";
import * as retrievePropObjects from "./../../constants/propObjectContainer.js";
import {withRouter} from "react-router";

class NavBarContainer extends Component {
	constructor (props) {
		super(props);
		this.state = {activeItem: this.getNavNameBasedOnRoute(props)};
	}

	getNavNameBasedOnRoute = (props) => {
		let currentRoute = null;
		
		Object.entries(retrievePropObjects.GET_NAV_BAR_ITEM).map((currentObj) => {
			if(currentObj[1].to === props.location.pathname){
				currentRoute = currentObj[1].name
			}
			return currentRoute;
		});

		return currentRoute;
	};

	handleItemClick = (e, {name}) => this.setState({activeItem: name});

	render() {
		const props = {
			getNavBarTabItems: {
				getNavBarLogo: <Menu.Item {...retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_LOGO} position="left" />,
				getDenStoriesMenuItem: (
					<Menu.Item
						active={this.state.activeItem === retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_DEN_STORIES.name}
						{...retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_DEN_STORIES}
					/>
				),
				getGalleryMenuItem: (
					<Menu.Item
						active={this.state.activeItem === retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_GALLERY.name}
						{...retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_GALLERY}
					/>
				),
				getGitHubNavItem: <Menu.Item {...retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_GIT_HUB} position="right" />,
				getLinkedinNavItem: <Menu.Item {...retrievePropObjects.GET_NAV_BAR_ITEM.NAV_BAR_LINKEDIN} />
			},
		};

		return <NavBarComponent {...props} />;
	}
}
export default withRouter(NavBarContainer);
