import React, { Component } from "react";
import CardComponent from "./CardComponent.jsx";
import { SEMANTIC_COLORS } from "./../../../constants/propObjectContainer.js";
import * as externalRoutes from "./../../../constants/externalRoutes";

class CardContainer extends Component {
	constructor(props){
		super();
		this.state = {
			isExtraHidden: true
		}
	}

	generateColor = () => {
		return Object.keys(SEMANTIC_COLORS)[Math.floor(Math.random() * 7) + 1];
	}

	toggleIsExtraHidden = () =>
	{
		this.setState( prevState => ( {isExtraHidden: !prevState} ) );
	}

	render() {
		const cardColor = this.generateColor();
		const props = {
			image: this.props.image,
			header: this.props.header,
			meta: <a href={externalRoutes.MET_MUSEUM} >{this.props.meta}</a>,//remove a tag
			description: this.props.description,
			extra: this.props.isExtraHidden ? null: this.props.extra,
			color: this.props.generateColor ? cardColor : this.props.color,
			onClick: ()=>this.toggleIsExtraHidden
		};
	
		return <CardComponent {...props} />;
	}
}

export default CardContainer;
